def split_coins(coins: Coin.getCoin | dict, num: int = 60) -> list:
    pms = []
    cntr = Counter()
    c = 0
    iterator = coins.get_dict().items() if type(coins) is Coin.getCoin else coins.items()
    for i, v in iterator:
        index = cntr.get_num()
        if index%num==0:c+=1
        if (c+1) >= len(pms): pms.append('')
        pms[c] += f'{index} - {i}: `{v:,}$`\n'
    while pms.count(''): pms.remove('')
    return pms
